import React from 'react';
import { 
  TruckIcon, 
  CheckCircleIcon, 
  StarIcon, 
  CurrencyDollarIcon,
  ClockIcon,
  ChartBarIcon
} from '@heroicons/react/24/outline';

const StatsCards = ({ stats }) => {
  const statItems = [
    {
      name: 'Total Deliveries',
      value: stats.totalDeliveries || 0,
      icon: TruckIcon,
      color: 'blue',
      description: 'All time deliveries'
    },
    {
      name: 'Success Rate',
      value: `${(stats.successRate || 0).toFixed(1)}%`,
      icon: CheckCircleIcon,
      color: 'green',
      description: 'Successful deliveries'
    },
    {
      name: 'Average Rating',
      value: (stats.averageRating || 0).toFixed(1),
      icon: StarIcon,
      color: 'yellow',
      description: 'Customer rating'
    },
    {
      name: 'Total Earnings',
      value: `₹${(stats.totalEarnings || 0).toFixed(0)}`,
      icon: CurrencyDollarIcon,
      color: 'purple',
      description: 'All time earnings'
    },
    {
      name: 'Active Orders',
      value: stats.activeOrders || 0,
      icon: ClockIcon,
      color: 'orange',
      description: 'Currently assigned'
    },
    {
      name: 'Today\'s Deliveries',
      value: stats.completedToday || 0,
      icon: ChartBarIcon,
      color: 'indigo',
      description: 'Completed today'
    },
  ];

  const getColorClasses = (color) => {
    const colors = {
      blue: 'bg-blue-500 text-blue-600 bg-blue-50',
      green: 'bg-green-500 text-green-600 bg-green-50',
      yellow: 'bg-yellow-500 text-yellow-600 bg-yellow-50',
      purple: 'bg-purple-500 text-purple-600 bg-purple-50',
      orange: 'bg-orange-500 text-orange-600 bg-orange-50',
      indigo: 'bg-indigo-500 text-indigo-600 bg-indigo-50',
    };
    return colors[color] || colors.blue;
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-6 gap-6">
      {statItems.map((item) => {
        const colorClasses = getColorClasses(item.color).split(' ');
        const iconBgColor = colorClasses[0];
        const textColor = colorClasses[1];
        const bgColor = colorClasses[2];
        
        return (
          <div key={item.name} className="bg-white rounded-lg shadow p-6 hover:shadow-md transition-shadow duration-200">
            <div className="flex items-center">
              <div className={`flex-shrink-0 p-3 rounded-lg ${bgColor}`}>
                <item.icon className={`h-6 w-6 ${textColor}`} />
              </div>
              <div className="ml-4 flex-1">
                <div className="flex items-baseline">
                  <p className="text-2xl font-semibold text-gray-900">
                    {item.value}
                  </p>
                </div>
                <p className="text-sm font-medium text-gray-600 truncate">
                  {item.name}
                </p>
                <p className="text-xs text-gray-500 mt-1">
                  {item.description}
                </p>
              </div>
            </div>
            
            {/* Progress indicator for some stats */}
            {item.name === 'Success Rate' && (
              <div className="mt-4">
                <div className="bg-gray-200 rounded-full h-2">
                  <div 
                    className={`h-2 rounded-full ${iconBgColor}`}
                    style={{ width: `${Math.min(stats.successRate || 0, 100)}%` }}
                  ></div>
                </div>
              </div>
            )}
            
            {item.name === 'Average Rating' && (
              <div className="mt-3 flex items-center">
                {[1, 2, 3, 4, 5].map((star) => (
                  <StarIcon
                    key={star}
                    className={`h-4 w-4 ${
                      star <= (stats.averageRating || 0)
                        ? 'text-yellow-400 fill-current'
                        : 'text-gray-300'
                    }`}
                  />
                ))}
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default StatsCards;
