import api from './api';

const ordersAPI = {
  // Get partner orders/assignments
  getPartnerOrders: async (partnerId, status = null) => {
    const params = status ? `?status=${status}` : '';
    const response = await api.get(`/delivery-partner-dashboard/assignments/${partnerId}${params}`);
    return response;
  },

  // Accept an order assignment
  acceptOrder: async (assignmentId, partnerId) => {
    const response = await api.put(`/delivery-partner-dashboard/assignments/${assignmentId}/accept`, {
      partnerId,
    });
    return response;
  },

  // Reject an order assignment
  rejectOrder: async (assignmentId, partnerId, rejectionReason) => {
    const response = await api.put(`/delivery-partner-dashboard/assignments/${assignmentId}/reject`, {
      partnerId,
      rejectionReason,
    });
    return response;
  },

  // Update order status during delivery
  updateOrderStatus: async (assignmentId, partnerId, status) => {
    const response = await api.put(`/delivery-partner-dashboard/assignments/${assignmentId}/status`, {
      partnerId,
      status,
    });
    return response;
  },

  // Get assignment details by ID
  getAssignmentDetails: async (assignmentId) => {
    const response = await api.get(`/order-assignments/${assignmentId}`);
    return response;
  },
};

export default ordersAPI;
