import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { 
  CurrencyDollarIcon, 
  ArrowTrendingUpIcon, 
  CalendarIcon,
  ChartBarIcon,
  BanknotesIcon
} from '@heroicons/react/24/outline';

const Earnings = () => {
  const { stats } = useSelector((state) => state.dashboard);
  const { orders } = useSelector((state) => state.orders);
  const [selectedPeriod, setSelectedPeriod] = useState('week');

  // Calculate earnings data based on selected period
  const getEarningsData = (period) => {
    const now = new Date();
    let startDate;
    
    switch (period) {
      case 'today':
        startDate = new Date(now.getFullYear(), now.getMonth(), now.getDate());
        break;
      case 'week':
        startDate = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
        break;
      case 'month':
        startDate = new Date(now.getFullYear(), now.getMonth(), 1);
        break;
      default:
        startDate = new Date(now.getTime() - 7 * 24 * 60 * 60 * 1000);
    }

    const filteredOrders = orders.filter(order => {
      if (!order.deliveryTime) return false;
      const orderDate = new Date(order.deliveryTime);
      return orderDate >= startDate && orderDate <= now;
    });

    const totalEarnings = filteredOrders.reduce((sum, order) => {
      return sum + (parseFloat(order.totalEarnings) || 0);
    }, 0);

    const totalOrders = filteredOrders.length;
    const avgEarningsPerOrder = totalOrders > 0 ? totalEarnings / totalOrders : 0;

    return {
      totalEarnings,
      totalOrders,
      avgEarningsPerOrder,
      orders: filteredOrders
    };
  };

  const currentPeriodData = getEarningsData(selectedPeriod);

  // Get daily earnings for the chart
  const getDailyEarnings = () => {
    const dailyData = {};
    const now = new Date();
    
    // Initialize last 7 days
    for (let i = 6; i >= 0; i--) {
      const date = new Date(now);
      date.setDate(date.getDate() - i);
      const dateKey = date.toISOString().split('T')[0];
      dailyData[dateKey] = { earnings: 0, orders: 0, date: date };
    }

    // Fill with actual data
    orders.forEach(order => {
      if (!order.deliveryTime) return;
      const orderDate = new Date(order.deliveryTime);
      const dateKey = orderDate.toISOString().split('T')[0];
      
      if (dailyData[dateKey]) {
        dailyData[dateKey].earnings += parseFloat(order.totalEarnings) || 0;
        dailyData[dateKey].orders += 1;
      }
    });

    return Object.values(dailyData);
  };

  const dailyEarnings = getDailyEarnings();
  const maxDailyEarnings = Math.max(...dailyEarnings.map(d => d.earnings), 100);

  const periods = [
    { id: 'today', name: 'Today' },
    { id: 'week', name: 'This Week' },
    { id: 'month', name: 'This Month' },
  ];

  const earningsBreakdown = [
    {
      name: 'Base Earnings',
      amount: currentPeriodData.totalEarnings * 0.7, // Assuming 70% is base
      color: 'bg-blue-500',
      description: 'Standard delivery fees'
    },
    {
      name: 'Distance Bonus',
      amount: currentPeriodData.totalEarnings * 0.2, // Assuming 20% is distance bonus
      color: 'bg-green-500',
      description: 'Extra pay for long distances'
    },
    {
      name: 'Time Bonus',
      amount: currentPeriodData.totalEarnings * 0.1, // Assuming 10% is time bonus
      color: 'bg-purple-500',
      description: 'Peak hour bonuses'
    },
  ];

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div className="bg-white rounded-lg shadow p-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-bold text-gray-900">Earnings</h1>
            <p className="text-gray-600 mt-1">
              Track your delivery earnings and performance
            </p>
          </div>
          
          {/* Period Selector */}
          <div className="flex space-x-2">
            {periods.map((period) => (
              <button
                key={period.id}
                onClick={() => setSelectedPeriod(period.id)}
                className={`px-4 py-2 text-sm font-medium rounded-md transition-colors duration-200 ${
                  selectedPeriod === period.id
                    ? 'bg-blue-600 text-white'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {period.name}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Earnings Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="flex-shrink-0 p-3 bg-green-100 rounded-lg">
              <CurrencyDollarIcon className="h-6 w-6 text-green-600" />
            </div>
            <div className="ml-4">
              <div className="text-2xl font-bold text-gray-900">
                ₹{currentPeriodData.totalEarnings.toFixed(0)}
              </div>
              <div className="text-sm text-gray-600">Total Earnings</div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="flex-shrink-0 p-3 bg-blue-100 rounded-lg">
              <ChartBarIcon className="h-6 w-6 text-blue-600" />
            </div>
            <div className="ml-4">
              <div className="text-2xl font-bold text-gray-900">
                {currentPeriodData.totalOrders}
              </div>
              <div className="text-sm text-gray-600">Completed Orders</div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="flex-shrink-0 p-3 bg-purple-100 rounded-lg">
              <ArrowTrendingUpIcon className="h-6 w-6 text-purple-600" />
            </div>
            <div className="ml-4">
              <div className="text-2xl font-bold text-gray-900">
                ₹{currentPeriodData.avgEarningsPerOrder.toFixed(0)}
              </div>
              <div className="text-sm text-gray-600">Avg per Order</div>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center">
            <div className="flex-shrink-0 p-3 bg-yellow-100 rounded-lg">
              <BanknotesIcon className="h-6 w-6 text-yellow-600" />
            </div>
            <div className="ml-4">
              <div className="text-2xl font-bold text-gray-900">
                ₹{(stats.totalEarnings || 0).toFixed(0)}
              </div>
              <div className="text-sm text-gray-600">All Time</div>
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Daily Earnings Chart */}
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-6">Daily Earnings (Last 7 Days)</h3>
          
          <div className="mb-6">
            <div className="flex items-end justify-between h-32 space-x-2">
              {dailyEarnings.map((day, index) => (
                <div key={index} className="flex-1 flex flex-col items-center">
                  <div className="w-full bg-gray-200 rounded-t-md relative" style={{ height: '100px' }}>
                    <div
                      className="bg-blue-500 rounded-t-md transition-all duration-300 hover:bg-blue-600"
                      style={{
                        height: `${(day.earnings / maxDailyEarnings) * 100}%`,
                        minHeight: day.earnings > 0 ? '4px' : '0px'
                      }}
                      title={`₹${day.earnings.toFixed(0)} from ${day.orders} orders`}
                    ></div>
                  </div>
                  <div className="text-xs text-gray-600 mt-2">
                    {day.date.toLocaleDateString('en-US', { weekday: 'short' })}
                  </div>
                  <div className="text-xs font-medium text-gray-900">
                    ₹{day.earnings.toFixed(0)}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Earnings Breakdown */}
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-6">Earnings Breakdown</h3>
          
          <div className="space-y-4">
            {earningsBreakdown.map((item, index) => (
              <div key={index} className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className={`w-4 h-4 rounded ${item.color}`}></div>
                  <div>
                    <div className="text-sm font-medium text-gray-900">{item.name}</div>
                    <div className="text-xs text-gray-500">{item.description}</div>
                  </div>
                </div>
                <div className="text-sm font-medium text-gray-900">
                  ₹{item.amount.toFixed(0)}
                </div>
              </div>
            ))}
          </div>

          <div className="mt-6 pt-4 border-t border-gray-200">
            <div className="flex items-center justify-between">
              <span className="text-base font-semibold text-gray-900">Total</span>
              <span className="text-base font-semibold text-gray-900">
                ₹{currentPeriodData.totalEarnings.toFixed(0)}
              </span>
            </div>
          </div>
        </div>
      </div>

      {/* Recent Earnings */}
      <div className="bg-white rounded-lg shadow">
        <div className="p-6 border-b border-gray-200">
          <h3 className="text-lg font-semibold text-gray-900">Recent Earnings</h3>
        </div>
        
        <div className="divide-y divide-gray-200">
          {currentPeriodData.orders.length === 0 ? (
            <div className="p-6 text-center">
              <CalendarIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h4 className="text-lg font-medium text-gray-900 mb-2">No Earnings Yet</h4>
              <p className="text-gray-600">
                Complete some deliveries to see your earnings here.
              </p>
            </div>
          ) : (
            currentPeriodData.orders.slice(0, 10).map((order) => (
              <div key={order.id} className="p-6 hover:bg-gray-50">
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3">
                      <h4 className="text-sm font-medium text-gray-900">
                        Order #{order.order?.id || order.id}
                      </h4>
                      <span className="px-2 py-1 text-xs font-medium bg-green-100 text-green-800 rounded-full">
                        Delivered
                      </span>
                    </div>
                    <div className="mt-1 text-sm text-gray-600">
                      {order.deliveryTime ? new Date(order.deliveryTime).toLocaleDateString('en-US', {
                        year: 'numeric',
                        month: 'short',
                        day: 'numeric',
                        hour: '2-digit',
                        minute: '2-digit'
                      }) : 'N/A'}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-lg font-semibold text-gray-900">
                      ₹{(order.totalEarnings || 0).toFixed(0)}
                    </div>
                    <div className="text-sm text-gray-600">
                      {order.deliveryDuration ? `${order.deliveryDuration} min` : 'N/A'}
                    </div>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Earnings;
