import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchOrders } from '../../store/slices/ordersSlice';
import ActiveOrders from '../../components/dashboard/ActiveOrders';
import { 
  FunnelIcon,
  MagnifyingGlassIcon,
  ClockIcon,
  CheckCircleIcon,
  XCircleIcon
} from '@heroicons/react/24/outline';

const Orders = () => {
  const dispatch = useDispatch();
  const { partner } = useSelector((state) => state.auth);
  const { orders, activeOrders, completedOrders, loading } = useSelector((state) => state.orders);
  
  const [activeTab, setActiveTab] = useState('active');
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    if (partner?.deliveryPartner?.id) {
      dispatch(fetchOrders({ partnerId: partner.deliveryPartner.id }));
    }
  }, [dispatch, partner]);

  const tabs = [
    { id: 'active', name: 'Active Orders', count: activeOrders.length, icon: ClockIcon },
    { id: 'completed', name: 'Completed', count: completedOrders.length, icon: CheckCircleIcon },
    { id: 'all', name: 'All Orders', count: orders.length, icon: FunnelIcon },
  ];

  const getFilteredOrders = () => {
    let filteredOrders = [];
    
    switch (activeTab) {
      case 'active':
        filteredOrders = activeOrders;
        break;
      case 'completed':
        filteredOrders = completedOrders;
        break;
      case 'all':
      default:
        filteredOrders = orders;
        break;
    }

    // Apply search filter
    if (searchTerm) {
      filteredOrders = filteredOrders.filter(order =>
        order.order?.id?.toString().includes(searchTerm) ||
        order.order?.user?.fullName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        order.order?.deliveryAddress?.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Apply status filter
    if (statusFilter !== 'all') {
      filteredOrders = filteredOrders.filter(order => order.status === statusFilter);
    }

    return filteredOrders;
  };

  const filteredOrders = getFilteredOrders();

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'ASSIGNED':
        return 'bg-blue-100 text-blue-800';
      case 'ACCEPTED':
        return 'bg-green-100 text-green-800';
      case 'PICKED_UP':
        return 'bg-yellow-100 text-yellow-800';
      case 'OUT_FOR_DELIVERY':
        return 'bg-purple-100 text-purple-800';
      case 'DELIVERED':
        return 'bg-green-100 text-green-800';
      case 'REJECTED':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Page Header */}
      <div className="bg-white rounded-lg shadow p-6">
        <h1 className="text-2xl font-bold text-gray-900">Orders Management</h1>
        <p className="text-gray-600 mt-1">
          Manage your delivery orders and track their status
        </p>
      </div>

      {/* Tabs */}
      <div className="bg-white rounded-lg shadow">
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8 px-6">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setActiveTab(tab.id)}
                className={`py-4 px-1 border-b-2 font-medium text-sm flex items-center space-x-2 ${
                  activeTab === tab.id
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                <tab.icon className="w-5 h-5" />
                <span>{tab.name}</span>
                <span className={`px-2 py-1 text-xs rounded-full ${
                  activeTab === tab.id
                    ? 'bg-blue-100 text-blue-600'
                    : 'bg-gray-100 text-gray-600'
                }`}>
                  {tab.count}
                </span>
              </button>
            ))}
          </nav>
        </div>

        {/* Filters */}
        <div className="p-6 border-b border-gray-200">
          <div className="flex flex-col sm:flex-row space-y-4 sm:space-y-0 sm:space-x-4">
            {/* Search */}
            <div className="flex-1">
              <div className="relative">
                <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search by order ID, customer name, or address..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
                />
              </div>
            </div>

            {/* Status Filter */}
            <div className="sm:w-48">
              <select
                value={statusFilter}
                onChange={(e) => setStatusFilter(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="all">All Statuses</option>
                <option value="ASSIGNED">Assigned</option>
                <option value="ACCEPTED">Accepted</option>
                <option value="PICKED_UP">Picked Up</option>
                <option value="OUT_FOR_DELIVERY">Out for Delivery</option>
                <option value="DELIVERED">Delivered</option>
                <option value="REJECTED">Rejected</option>
              </select>
            </div>
          </div>
        </div>

        {/* Orders Content */}
        <div className="p-6">
          {activeTab === 'active' ? (
            <ActiveOrders orders={filteredOrders} />
          ) : (
            <div className="space-y-4">
              {filteredOrders.length === 0 ? (
                <div className="text-center py-12">
                  <ClockIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                  <h4 className="text-lg font-medium text-gray-900 mb-2">No Orders Found</h4>
                  <p className="text-gray-600">
                    {searchTerm || statusFilter !== 'all' 
                      ? 'Try adjusting your search or filter criteria.'
                      : 'You don\'t have any orders in this category yet.'
                    }
                  </p>
                </div>
              ) : (
                filteredOrders.map((order) => (
                  <div key={order.id} className="border border-gray-200 rounded-lg p-4 hover:shadow-md transition-shadow duration-200">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center space-x-3 mb-2">
                          <h4 className="text-lg font-medium text-gray-900">
                            Order #{order.order?.id || order.id}
                          </h4>
                          <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(order.status)}`}>
                            {order.status.replace('_', ' ')}
                          </span>
                        </div>
                        
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 text-sm text-gray-600">
                          <div>
                            <p><span className="font-medium">Customer:</span> {order.order?.user?.fullName || 'N/A'}</p>
                            <p><span className="font-medium">Phone:</span> {order.order?.user?.contactNumber || 'N/A'}</p>
                            <p><span className="font-medium">Address:</span> {order.order?.deliveryAddress || 'N/A'}</p>
                          </div>
                          <div>
                            <p><span className="font-medium">Assigned:</span> {formatDate(order.assignedAt)}</p>
                            {order.acceptedAt && (
                              <p><span className="font-medium">Accepted:</span> {formatDate(order.acceptedAt)}</p>
                            )}
                            {order.deliveryTime && (
                              <p><span className="font-medium">Delivered:</span> {formatDate(order.deliveryTime)}</p>
                            )}
                            <p><span className="font-medium">Amount:</span> ₹{order.order?.totalAmount || 0}</p>
                          </div>
                        </div>

                        {order.rejectionReason && (
                          <div className="mt-3 p-2 bg-red-50 rounded-md">
                            <p className="text-sm text-red-800">
                              <span className="font-medium">Rejection Reason:</span> {order.rejectionReason}
                            </p>
                          </div>
                        )}

                        {order.customerFeedback && (
                          <div className="mt-3 p-2 bg-blue-50 rounded-md">
                            <p className="text-sm text-blue-800">
                              <span className="font-medium">Customer Feedback:</span> {order.customerFeedback}
                            </p>
                            {order.customerRating && (
                              <div className="flex items-center mt-1">
                                <span className="text-sm text-blue-800 mr-2">Rating:</span>
                                {[1, 2, 3, 4, 5].map((star) => (
                                  <svg
                                    key={star}
                                    className={`w-4 h-4 ${
                                      star <= order.customerRating
                                        ? 'text-yellow-400 fill-current'
                                        : 'text-gray-300'
                                    }`}
                                    fill="currentColor"
                                    viewBox="0 0 20 20"
                                  >
                                    <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                                  </svg>
                                ))}
                              </div>
                            )}
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Orders;
