import api from './api';

const dashboardAPI = {
  // Get partner profile by auth ID
  getPartnerProfile: async (authId) => {
    const response = await api.get(`/delivery-partner-dashboard/profile/${authId}`);
    return response;
  },

  // Get partner dashboard statistics
  getPartnerStats: async (partnerId) => {
    const response = await api.get(`/delivery-partner-dashboard/stats/${partnerId}`);
    return response;
  },

  // Update availability status
  updateAvailability: async (partnerId, isAvailable, availabilityStatus) => {
    const response = await api.put(`/delivery-partner-dashboard/availability/${partnerId}`, {
      isAvailable,
      availabilityStatus,
    });
    return response;
  },

  // Update partner location
  updateLocation: async (partnerId, latitude, longitude) => {
    const response = await api.put(`/delivery-partner-dashboard/location/${partnerId}`, {
      latitude,
      longitude,
    });
    return response;
  },
};

export default dashboardAPI;
