import React from 'react';
import { useDispatch } from 'react-redux';
import { acceptOrder, rejectOrder, updateOrderStatus } from '../../store/slices/ordersSlice';
import { toast } from 'react-toastify';
import { 
  ClockIcon, 
  MapPinIcon, 
  PhoneIcon,
  CheckIcon,
  XMarkIcon,
  TruckIcon
} from '@heroicons/react/24/outline';

const ActiveOrders = ({ orders }) => {
  const dispatch = useDispatch();

  const handleAcceptOrder = async (order) => {
    try {
      await dispatch(acceptOrder({
        assignmentId: order.id,
        partnerId: order.deliveryPartner.id
      })).unwrap();
      toast.success('Order accepted successfully!');
    } catch (error) {
      toast.error(error || 'Failed to accept order');
    }
  };

  const handleRejectOrder = async (order) => {
    const reason = prompt('Please provide a reason for rejection (optional):');
    if (reason === null) return; // User cancelled
    
    try {
      await dispatch(rejectOrder({
        assignmentId: order.id,
        partnerId: order.deliveryPartner.id,
        rejectionReason: reason || 'No reason provided'
      })).unwrap();
      toast.success('Order rejected');
    } catch (error) {
      toast.error(error || 'Failed to reject order');
    }
  };

  const handleUpdateStatus = async (order, newStatus) => {
    try {
      await dispatch(updateOrderStatus({
        assignmentId: order.id,
        partnerId: order.deliveryPartner.id,
        status: newStatus
      })).unwrap();
      toast.success(`Order status updated to ${newStatus.toLowerCase().replace('_', ' ')}`);
    } catch (error) {
      toast.error(error || 'Failed to update order status');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'ASSIGNED':
        return 'bg-blue-100 text-blue-800';
      case 'ACCEPTED':
        return 'bg-green-100 text-green-800';
      case 'PICKED_UP':
        return 'bg-yellow-100 text-yellow-800';
      case 'OUT_FOR_DELIVERY':
        return 'bg-purple-100 text-purple-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusActions = (order) => {
    switch (order.status) {
      case 'ASSIGNED':
        return (
          <div className="flex space-x-2">
            <button
              onClick={() => handleAcceptOrder(order)}
              className="flex items-center px-3 py-1 bg-green-600 text-white text-sm rounded-md hover:bg-green-700 transition-colors duration-200"
            >
              <CheckIcon className="w-4 h-4 mr-1" />
              Accept
            </button>
            <button
              onClick={() => handleRejectOrder(order)}
              className="flex items-center px-3 py-1 bg-red-600 text-white text-sm rounded-md hover:bg-red-700 transition-colors duration-200"
            >
              <XMarkIcon className="w-4 h-4 mr-1" />
              Reject
            </button>
          </div>
        );
      case 'ACCEPTED':
        return (
          <button
            onClick={() => handleUpdateStatus(order, 'PICKED_UP')}
            className="flex items-center px-3 py-1 bg-blue-600 text-white text-sm rounded-md hover:bg-blue-700 transition-colors duration-200"
          >
            <TruckIcon className="w-4 h-4 mr-1" />
            Mark Picked Up
          </button>
        );
      case 'PICKED_UP':
        return (
          <button
            onClick={() => handleUpdateStatus(order, 'OUT_FOR_DELIVERY')}
            className="flex items-center px-3 py-1 bg-purple-600 text-white text-sm rounded-md hover:bg-purple-700 transition-colors duration-200"
          >
            <TruckIcon className="w-4 h-4 mr-1" />
            Out for Delivery
          </button>
        );
      case 'OUT_FOR_DELIVERY':
        return (
          <button
            onClick={() => handleUpdateStatus(order, 'DELIVERED')}
            className="flex items-center px-3 py-1 bg-green-600 text-white text-sm rounded-md hover:bg-green-700 transition-colors duration-200"
          >
            <CheckIcon className="w-4 h-4 mr-1" />
            Mark Delivered
          </button>
        );
      default:
        return null;
    }
  };

  const formatTime = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (!orders || orders.length === 0) {
    return (
      <div className="bg-white rounded-lg shadow p-6">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Active Orders</h3>
        <div className="text-center py-12">
          <TruckIcon className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <h4 className="text-lg font-medium text-gray-900 mb-2">No Active Orders</h4>
          <p className="text-gray-600">
            You don't have any active orders right now. Make sure your availability is set to "Available" to receive new orders.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="bg-white rounded-lg shadow">
      <div className="p-6 border-b border-gray-200">
        <h3 className="text-lg font-semibold text-gray-900">
          Active Orders ({orders.length})
        </h3>
      </div>
      
      <div className="divide-y divide-gray-200">
        {orders.map((order) => (
          <div key={order.id} className="p-6 hover:bg-gray-50 transition-colors duration-200">
            <div className="flex items-start justify-between">
              <div className="flex-1">
                {/* Order Header */}
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <h4 className="text-lg font-medium text-gray-900">
                      Order #{order.order?.id || order.id}
                    </h4>
                    <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(order.status)}`}>
                      {order.status.replace('_', ' ')}
                    </span>
                  </div>
                  <div className="text-sm text-gray-500">
                    Assigned: {formatTime(order.assignedAt)}
                  </div>
                </div>

                {/* Customer Info */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
                  <div className="space-y-2">
                    <div className="flex items-center text-sm text-gray-600">
                      <MapPinIcon className="w-4 h-4 mr-2" />
                      <span>
                        {order.order?.deliveryAddress || 'Delivery address not available'}
                      </span>
                    </div>
                    <div className="flex items-center text-sm text-gray-600">
                      <PhoneIcon className="w-4 h-4 mr-2" />
                      <span>
                        {order.order?.user?.contactNumber || 'Contact not available'}
                      </span>
                    </div>
                  </div>
                  
                  <div className="space-y-2">
                    <div className="flex items-center text-sm text-gray-600">
                      <ClockIcon className="w-4 h-4 mr-2" />
                      <span>
                        Expected: {order.order?.expectedDeliveryTime || 'ASAP'}
                      </span>
                    </div>
                    <div className="text-sm text-gray-600">
                      <span className="font-medium">Total: ₹{order.order?.totalAmount || 0}</span>
                    </div>
                  </div>
                </div>

                {/* Special Instructions */}
                {order.specialInstructions && (
                  <div className="mb-4 p-3 bg-yellow-50 rounded-lg">
                    <p className="text-sm text-yellow-800">
                      <span className="font-medium">Special Instructions:</span> {order.specialInstructions}
                    </p>
                  </div>
                )}

                {/* Order Items Preview */}
                {order.order?.orderItems && order.order.orderItems.length > 0 && (
                  <div className="mb-4">
                    <p className="text-sm text-gray-600 mb-2">
                      Items ({order.order.orderItems.length}):
                    </p>
                    <div className="flex flex-wrap gap-2">
                      {order.order.orderItems.slice(0, 3).map((item, index) => (
                        <span key={index} className="px-2 py-1 bg-gray-100 text-gray-700 text-xs rounded">
                          {item.product?.name || 'Product'} x{item.quantity}
                        </span>
                      ))}
                      {order.order.orderItems.length > 3 && (
                        <span className="px-2 py-1 bg-gray-100 text-gray-700 text-xs rounded">
                          +{order.order.orderItems.length - 3} more
                        </span>
                      )}
                    </div>
                  </div>
                )}
              </div>
            </div>

            {/* Action Buttons */}
            <div className="flex justify-end mt-4">
              {getStatusActions(order)}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ActiveOrders;
